    public function getSourceContext()
    {
        return new Source("{% extends 'base.html.twig' %}

{% block title %}Edit Reclamation{% endblock %}

{% block content %}
    <div class=\"container my-5\">
        <div class=\"row justify-content-center\">
            <div class=\"col-md-8\">
                {% for message in app.flashes('success') %}
                    <div class=\"alert alert-success\">
                        {{ message }}
                    </div>
                {% endfor %}

                <div class=\"card\">
                    <div class=\"card-header bg-primary text-white\">
                        <h1 class=\"card-title\">Edit Reclamation</h1>
                    </div>
                    <div class=\"card-body\">
                        <form method=\"post\" action=\"{{ path('app_reclamation_edit', {'id': reclamation.id}) }}\" novalidate>
                            {{ form_start(form) }}
                            {{ form_row(form.nom, { 'attr': {'class': 'form-control mb-3'} }) }}
                            {{ form_row(form.prenom, { 'attr': {'class': 'form-control mb-3'} }) }}
                            {{ form_row(form.email, { 'attr': {'class': 'form-control mb-3'} }) }}
                            {{ form_row(form.numeroMobile, { 'attr': {'class': 'form-control mb-3'} }) }}
                            {{ form_row(form.description, { 'attr': {'class': 'form-control mb-3'} }) }}
                            {{ form_row(form.objet, { 'attr': {'class': 'form-control mb-3'} }) }}
                    </div>
                    <div class=\"card-footer\">
                        <button type=\"submit\" class=\"btn btn-primary\">Update</button>
                        <a href=\"{{ path('app_reclamation_search') }}\" class=\"btn btn-secondary\">Back to List</a>
                    </div>
                    {{ form_end(form) }}
                </div>
            </div>
        </div>
    </div>
{% endblock %}


", "reclamation/edit.html.twig", "C:\\Users\\hassa\\Videos\\CoursifyWeb\\templates\\reclamation\\edit.html.twig");
